import React from "react";
import { useDispatch } from "react-redux";
import { toggleSongPrivacy } from "../songs"; // thunk we’ll create later
import "./SongPrivacyToggle.css";
function SongPrivacyToggle({ song }) {
  const dispatch = useDispatch();

    const songId = song.id;
  const handleToggle = () => {
   fetch(`/api/songs/${songId}/privacy`, {
  method: "PATCH",
  headers: { "Content-Type": "application/json" },
  body: JSON.stringify({ is_private: true }),
});
  };

  return (
    <div className="song-item">
      <div className="song-info">
        <span className="song-title">{song.title}</span>
      </div>
      <div className="privacy-toggle">
        <button
          onClick={handleToggle}
          className={`privacy-btn ${song.is_private ? "private" : "public"}`}
        >
          {song.is_private ? "Make Public" : "Make Private"}
        </button>
      </div>
    </div>
  );
}

export default SongPrivacyToggle;
