import { useDispatch } from "react-redux";
import { toggleSongPrivacy } from "../../store/song.store";

function SongPrivacyToggle({ song }) {
  const dispatch = useDispatch();

  const handleToggle = () => {
    dispatch(toggleSongPrivacy(song.id, !song.is_private));
  };

  return (
    <button onClick={handleToggle}>
      {song.is_private ? "Make Public" : "Make Private"}
    </button>
  );
}
