import React, { useEffect } from "react";
import { useDispatch } from "react-redux";
import { toggleSongPrivacy } from "../../store/song";
import "./SongPrivacyToggle.css";
function SongPrivacyToggle({ song }) {
  const dispatch = useDispatch();

    const songId = song.id;
    useEffect(() => {
      // Optionally, you could fetch the latest song data here if needed
    }, [songId]);
  const handleToggle = () => {
   dispatch(toggleSongPrivacy(songId, !song.is_private));   

  };

  return (
    <div className="song-item">
      <div className="song-info">
        <span className="song-title">{song.title}</span>
      </div>
      <div className="privacy-toggle">
        <button
          onClick={handleToggle}
          className={`privacy-btn ${song.is_private ? "private" : "public"}`}
        >
          {song.is_private ? "Make Public" : "Make Private"}
        </button>
      </div>
    </div>
  );
}

export default SongPrivacyToggle;
