import React, { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { useHistory } from "react-router-dom";
import { getAllMusicians, addingFullMusician } from "../../store/musician";

const MusicianFormRefactor = () => {

    const history = useHistory();
    const dispatch = useDispatch();

    const user = useSelector((state) => state.session.user);

    // const [musicians, setMusicians] = useState([]);
    const [musician_name, setMusicianName] = useState("");
    const [profile_img, setProfileImg] = useState('');
    const [biography, setBiography] = useState("");


    const userId = user.id
    const onSubmit = async (e) => {
        e.preventDefault();
        const formData = new FormData();

        formData.append("profile_img", profile_img);
        const res = await fetch("/api/musicians/new-picture", {
            method: "POST",
            body: formData,
        });
        if (res.ok) {
            let profile_img = await res.json();
            dispatch(postNewMusician(profile_img, biography, Number(userId), musician_name));
            dispatch(getAllMusicians());
        }
        history.push(`/users/${user.id}`);
    };

    const updateProfileImg = (e) => {
        const file = e.target.files[0];
        setProfileImg(file);
    };

    return (
        <div className="outer_card">
            <h1 id="add-musician-text">Add A Musician</h1>
            <form className="musician-form" onSubmit={onSubmit}>
                <div className="input_container">
                    <label htmlFor="musician_name" style={{ color: "white" }}>
                        Musician Name
                    </label>
                    <input
                        type="text"
                        name="musician_name"
                        placeholder="Musician Name"
                        onChange={(e) => setMusicianName(e.target.value)}
                        value={musician_name}
                    />
                </div>
                <label htmlFor="add-profile-pic" style={{ color: "white" }}>
                    Add Profile Picture
                </label>
                <input
                    style={{ color: "white", fontSize: "15px", fontWeight: "500" }}
                    type="file"
                    accept="image/*"
                    name="profile_img"

                    onChange={updateProfileImg}
                />
                <label htmlFor="biography" style={{ color: "white" }}>
                    Biography
                </label>
                <textarea
                    name="biography"
                    type="text"
                    placeholder="biography..."
                    onChange={(e) => setBiography(e.target.value)}
                    value={biography}
                />
                <button className="submit" type="submit" id="create_musician">
                    Submit
                </button>
            </form>
        </div>
    );
};
export default MusicianFormRefactor;

// Sample code from PayChecker.js for context
// PayChecker.js

function calculateBasePay(hoursWorked, hourlyRate = 15) {
    return hoursWorked * hourlyRate;
}

function calculateOvertimePay(hoursWorked, hourlyRate = 15) {
    const overtimeHours = Math.max(0, hoursWorked - 40);
    return overtimeHours * hourlyRate * 1.5;
}

function calculateTotalPay({
    hoursWorked,
    serviceCharge = 0,
    gratuity = 0,
    shiftPremium = 0,
    pto = 0,
    holidayPay = 0,
    predictPay = 0,
}) {
    const basePay = calculateBasePay(hoursWorked);
    const overtimePay = calculateOvertimePay(hoursWorked);
    return (
        basePay +
        overtimePay +
        serviceCharge +
        gratuity +
        shiftPremium +
        pto +
        holidayPay +
        predictPay
    );
}

const week1 = {
    hoursWorked: 44.75,
    serviceCharge: 638.81,
    gratuity: 226.55,
    shiftPremium: 0,
    pto: 0,
    holidayPay: 0,
    predictPay: 0,
};

const week2 = {
    hoursWorked: 28.18,
    serviceCharge: 530.71,
    gratuity: 150.00,
    shiftPremium: 0,
    pto: 0,
    holidayPay: 0,
    predictPay: 0,
};
