import React from "react";
import { useDispatch } from "react-redux";
import { toggleSongPrivacy } from "../store/songs"; // thunk we’ll create later

function SongPrivacyToggle({ song }) {
  const dispatch = useDispatch();

  const handleToggle = () => {
    dispatch(toggleSongPrivacy(song.id, !song.is_private));
  };

  return (
    <div className="song-item">
      <div className="song-info">
        <span className="song-title">{song.title}</span>
      </div>
      <div className="privacy-toggle">
        <button
          onClick={handleToggle}
          className={`privacy-btn ${song.is_private ? "private" : "public"}`}
        >
          {song.is_private ? "Make Public" : "Make Private"}
        </button>
      </div>
    </div>
  );
}

export default SongPrivacyToggle;
